pode ser feito desta forma também
<script>
jQuery(document).ready(function() {
var requestRunning = false;
$("#rascunho").button().click(function() {
if (requestRunning) {
return;
}
jQuery('#savepa').submit(function() {
var dados = jQuery(this).serialize();
jQuery.ajax({
type: "POST",
url: "savepa.php?id=" + $("#id").val(),
cache: false,
data: dados,
success: function(data) {
//alert(data);
alert('Dados Salvo com Sucesso!!!!!!');
}
});
cache = false;
requestRunning = true;
return false;
});
});

